import React, { useState } from "react";
import axios from "axios";

const SignIn = (props) => {
	const [email, setEmail] = useState("");
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [bookingRef, setBookingRef] = useState("");
	const [loginError, setLoginError] = useState(false);

	function userValidation() {
		return (
			email.length > 0 &&
			firstName.length > 0 &&
			lastName.length > 0 &&
			bookingRef.length > 0
		);
	}
	const signInAPI = async () => {
		try {
			const response = await axios.post(`http://localhost:5002/login`, {
				first_name: firstName,
				last_name: lastName,
				email: email,
				booking_ref: bookingRef,
			});

			if (response.data.loginSuccessful) {
				props.setPage(2);
			} else {
				setLoginError(true);
			}
			//if valid, then move on to uservalidation
		} catch (error) {
			console.log(error);
		}
	};
	// function onSubmit(e) {
	// 	e.preventDefault();
	// 	signInAPI();
	// 	// const isValid = userValidation();
	// }

	return (
		<>
			<div className="signIn">
				<h1>Sign in to view your booking</h1>
				<p>
					Please use the details you provided when booking your tour. Unsure?
					Check your confirmation email.
				</p>
				<p>First Name</p>
				<input
					// onSubmit={onSubmit}
					id="firstName"
					value={firstName}
					type="text"
					onChange={(e) => setFirstName(e.target.value)}
				/>
				<p>Last Name</p>
				<input
					// onSubmit={onSubmit}
					id="lastName"
					value={lastName}
					type="text"
					onChange={(e) => setLastName(e.target.value)}
				/>
				<p>Booking Reference</p>
				<input
					// onSubmit={onSubmit}
					id="bookingRef"
					value={bookingRef}
					type="text"
					placeholder="TBT-"
					onChange={(e) => setBookingRef(e.target.value)}
				/>
				<p>Email</p>
				<input
					// onSubmit={onSubmit}
					id="email"
					type="email"
					onChange={(e) => setEmail(e.target.value)}
				/>
			</div>
			<button
				className="signInButton"
				onClick={() => signInAPI()}
				type="submit"
				// disabled={!userValidation()}
			>
				View My Booking
			</button>
			{loginError && <h1>Please check your details</h1>}
			<div>
				<a
					className="signInProblem"
					// onSubmit={onSubmit}
					onClick={() => {
						props.setPage(0);
					}}
				>
					Problems Signing In?
				</a>
			</div>
		</>
	);
};

export default SignIn;
