import { useEffect, useState } from "react";
import axios from "axios";

const Forecast = () => {
	const [forecastWeather, setForecastWeather] = useState("");
	const [forecastTemperature, setForecastTemperature] = useState(0);
	const [iconCode, setIconCode] = useState("");
	const [forecastDay, setForecastDay] = useState("");

	const getForecast = async () => {
		try {
			const res = await axios.get(
				`http://api.openweathermap.org/data/2.5/forecast?lat=55.95206&lon=-3.19648&appid=a2e9f900ab03f983ca54c821ab03cb37&units=metric`
			); //hardcoding this to EDINBURGH weather. Potentially hardcode this to a button [Edinburgh | Glasgow | Inverness]
			setForecastWeather(res.data.list.weather[0].main);
			setForecastTemperature(Math.round(res.data.list.temp));
			setIconCode(res.data.weather[0].icon);
			setForecastDay(res.data.list.dt);
		} catch (err) {
			console.error(err);
		}
	};
	return (
		<>
			<section className="forecast">
				<div className="forecastContainer">
					<div className="forecastWeather">
						<h2>{forecastTemperature}ÂºC</h2>
						<h2>{forecastWeather}</h2>
						<h4>{forecastDay}</h4>
						<img
							// src={`http://openweathermap.org/img/wn/${iconCode}@2x.png`}
							alt="weather icon"
						/>
					</div>
				</div>
			</section>
		</>
	);
};

export default Forecast;

