import React, { useState } from "react";

const SignIn = (props) => {
	const [email, setEmail] = useState("");
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [bookingRef, setBookingRef] = useState("");
	const [firstNameError, setFirstNameError] = useState({});
	const [lastNameError, setLastNameError] = useState({});
	const [emailError, setEmailError] = useState({});
	const [bookingRefError, setBookingRefError] = useState({});

	function userValidation() {
		//return email.length > 0 && name.length > 0 && bookingRef.length > 0;
		const firstNameError = {};
		const lastNameError = {};
		const emailError = {};
		const bookingRefError = {};
		let isValid = true;

		if (firstName.length < 3 || firstName.length > 15) {
			firstNameError.firstNameMessage =
				"Your first name must be between 3-15 characters";
			isValid = false;
		}
		if (lastName.length < 2 || lastName.length > 20) {
			lastNameError.lastNameMessage =
				"Last name must be between 2-20 characters.";
			isValid = false;
		}
		if (
			bookingRef.length < 8 ||
			bookingRef.length > 20 ||
			!bookingRef.includes("TBT-")
		) {
			bookingRefError.bookingRefMessage =
				"Your Booking Reference must be between 8-20 characters and start TBT-.";
			isValid = false;
		}
	}
	function onSubmit(e) {
		e.preventDefault();
		const isValid = userValidation();
	}

	return (
		<>
			<div className="signIn">
				<h1>Sign in to view your booking</h1>
				<p>
					Please use the details you provided when booking your tour. Unsure?
					Check your confirmation email.
				</p>
				<p>First Name</p>
				<input
					onSubmit={onSubmit}
					id="firstName"
					value={firstName}
					type="text"
					onChange={(e) => setFirstName(e.target.value)}
				/>
				{Object.keys(firstNameError).map((key) => {
					return <div key={key}>{firstNameError[key]}</div>;
				})}
				<p>Last Name</p>
				<input
					onSubmit={onSubmit}
					id="lastName"
					value={lastName}
					type="text"
					onChange={(e) => setLastName(e.target.value)}
				/>
				{Object.keys(lastNameError).map((key) => {
					return <div key={key}>{lastNameError[key]}</div>;
				})}
				<p>Booking Reference</p>
				<input
					onSubmit={onSubmit}
					id="bookingRef"
					value={bookingRef}
					type="text"
					placeholder="TBT-"
					onChange={(e) => setBookingRef(e.target.value)}
				/>
				<p>Email</p>
				<input
					onSubmit={onSubmit}
					id="email"
					value={email}
					type="email"
					onChange={(e) => setEmail(e.target.value)}
				/>
				<p>
					I agree to the{" "}
					<a href="https://www.timberbush-tours.co.uk/terms-conditions">
						Terms
					</a>{" "}
					<input type="checkbox" />
				</p>
			</div>
			<button
				onClick={() => props.setPage(3)}
				type="submit"
				disabled={!userValidation()}
			>
				View My Booking
			</button>
		</>
	);
};

export default SignIn;
