import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
	faMale,
	faPhone,
	faAt,
	faComments,
} from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

const ContactForm = () => {
	const handleSubmit = (e) => {
		e.preventDefault();
		const firstName = document.getElementById("firstName").value;
		const lastName = document.getElementById("lastName").value;
		const tel = document.getElementById("tel").value;
		const email = document.getElementById("email").value;
		const customerMessage = document.getElementById("customerMessage").value;
		axios({
			method: "POST",
			url: "http://localhost:5002/send",
			data: {
				firstName: firstName,
				lastName: lastName,
				email: email,
				tel: tel,
				customerMessage: customerMessage,
			},
		}).then((response) => {
			if (response.data.msg === "success") {
				alert("message sent!");
				this.resetForm();
			} else {
				alert("message could not be sent");
			}
		});
	};
	const resetForm = () => {
		document.getElementById("contactForm").reset();
	};

	return (
		<>
			<form id="contactForm" method="POST">
				<h2>Send us a Message</h2>
				<div className="formGroup">
					<label for="firstName">
						First Name<span>*</span>
					</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faMale" icon={faMale} />
						</span>
						<input type="text" name="firstName" id="firstName" />
					</div>
				</div>
				<div className="formGroup">
					<label for="lastName">
						Last Name<span>*</span>
					</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faMale" icon={faMale} />
						</span>
						<input type="text" name="lastName" id="lastName" />
					</div>
				</div>
				<div className="formGroup">
					<label for="phone">Phone</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faPhone" icon={faPhone} />
						</span>
						<input type="tel" name="tel" id="tel" />
					</div>
				</div>
				<div className="formGroup">
					<label for="email">Email</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faAt" icon={faAt} />
						</span>
						<input type="email" name="email" id="email" />
					</div>
				</div>
				<div className="formGroup">
					<label for="message">Your Message</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faComments" icon={faComments} />
						</span>
						<textarea
							id="customerMessage"
							className="customerMessage"
							required=""
							rows="3"
						></textarea>
					</div>
				</div>
				<div>
					<button
						type="submit"
						className="buttonContact"
						onClick="handleSubmit()"
					>
						Send
					</button>
				</div>
			</form>
		</>
	);
};

export default ContactForm;
