import React from "react";
import { useState } from "react";

const Terms = () => {
	const [page, setPage] = useState(1);
	return (
		<>
			<h1>Our Terms and Conditions</h1>
			<p>
				Thank you for choosing to travel with us. Please find our terms and
				conditions below.
			</p>

			<h4>Cancellation policy</h4>
			<p>
				The company reserves the right to cancel tours when the minimum number
				of passengers is not met. Minimum number of passengers required is 6.
			</p>
			<h4>Cancellation Charges</h4>
			<p>
				When the customer cancels his/her reservation, the following terms and
				conditions apply:
			</p>
			<h5>One Day Tours</h5>
			<p>
				For cancellations made within 24 hours before departure, a 100% refund
				will be given.
			</p>
			<h5>Multi Day Tours</h5>
			<p>Cancellations must be made giving 7 days notice</p>
		</>
	);
};

export default Terms;
