import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";
import { useTransition, animated } from "react-spring";

const HamburgerMenu = () => {
	const [showMenu, setShowMenu] = useState(false); //deciding whether we will show the menu. Menu always loads in closed.

	const transitions = useTransition(showMenu, null, {
		from: { position: "absolute", opacity: 0 },
		enter: { opacity: 1 },
		leave: { opacity: 0 },
	});

	let menu;
	let menuMask;

	if (showMenu) {
		menu = <div className="burgerMenu">The Menu</div>;

		menuMask = (
			<div className="menuMask" onClick={() => setShowMenu(false)}></div>
		);
	} //to make menu more pronounced

	return (
		<>
			<span className="burgerIcon">
				<FontAwesomeIcon
					icon={faBars}
					size="2x"
					onClick={() => setShowMenu(!showMenu)} //toggle
				/>
			</span>
			{/* {menuMask}
			{menu} */}
		</>
	);
};

export default HamburgerMenu;
