import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";
import Homepage from "./Homepage";
import SignIn from "./SignIn";

const HamburgerMenu = () => {
	const [page, setPage] = useState(0);
	const [showMenu, setShowMenu] = useState(false); //deciding whether we will show the menu. Menu always loads in closed.
	let menu;
	let menuMask;

	if (showMenu) {
		menu = (
			<div className="burgerMenu">
				<div>{page === 0 && <div onClick={() => setPage(1)}>Sign In</div>}</div>
				<div>{page > 1 && <div onClick={() => setPage(2)}>Homepage</div>}</div>
			</div>
		);

		menuMask = (
			<div className="menuMask" onClick={() => setShowMenu(false)}></div>
		);
	} //to make menu more pronounced

	return (
		<>
			<span className="burgerIcon">
				<FontAwesomeIcon
					icon={faBars}
					size="2x"
					onClick={() => setShowMenu(!showMenu)} //toggle
				/>
			</span>

			{menuMask}
			{menu}
		</>
	);
};

export default HamburgerMenu;
