import React, { useState } from "react";

const SignIn = (props) => {
	const [error, setError] = useState("");
	const [details, setDetails] = useState({
		name: "",
		email: "",
		bookingRef: "",
	});
	const adminUser = {
		email: "admin@admin.com",
		bookingRef: "12345",
	};

	const submitHandler = (e) => {
		e.preventDefault();
		const login = userValidation();
		//Want the user validation HERE.
	};
	const userValidation = () => {
		if (
			details.email === adminUser.email &&
			details.bookingRef === adminUser.bookingRef
		) {
			console.log("Great success! You're logged in!");
		} else {
			console.log("details do not match");
		}
	};

	return (
		<>
			<form onSubmit={submitHandler}>
				<div className="formInner">
					<h2>Sign In</h2>
					{/* ERROR MESSAGE HERE */}
					<div className="formGroup">
						<label for="name">Name:</label>
						<input
							type="text"
							name="name"
							id="name"
							onChange={(e) => setDetails({ details, name: e.target.value })} //once changed, call function, pass event through object 'details' - updates setDetails and new value for name
							value={details.name}
						/>
					</div>
					<div className="formGroup">
						<label for="email">Email:</label>
						<input
							type="email"
							name="email"
							id="email"
							onChange={(e) => setDetails({ details, email: e.target.value })}
							value={details.email}
						/>
					</div>
					<div className="formGroup">
						<label for="bookingRef">Booking Reference:</label>
						<input
							type="text"
							name="bookingRef"
							id="bookingRef"
							onChange={(e) =>
								setDetails({ details, bookingRef: e.target.value })
							}
							value={details.bookingRef}
						/>
					</div>
					<button
						type="submit"
						onClick={() => {
							props.setPage(3);
						}}
					>
						View My Booking
					</button>
				</div>
			</form>
		</>
	);
};

export default SignIn;
