import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStopwatch } from "@fortawesome/free-solid-svg-icons";
import SVGCircle from "./SVGCircle";
//use effect listens to change of countdown date, stops timer and restarts again.
//break up into c gets data, renders interface. pass in

const Countdown = () => {
	//SVG CIRCLE - Working out the radius
	const daysRadius = mapNumber(timerDays, 30, 0, 0, 360);
	const hoursRadius = mapNumber(timerHours, 24, 0, 0, 360);
	const minutesRadius = mapNumber(timerMinutes, 60, 0, 0, 360);
	const secondsRadius = mapNumber(Math.round(timerSeconds), 60, 0, 0, 360);

	return (
		<>
			<h2 className="countdownHeader">Countdown...</h2>
			<FontAwesomeIcon className="stopWatch" icon={faStopwatch} size="2x" />

			<div className="countdownContainer">
				{timerDays >= 0 && (
					<div className="countdownItem">
						<SVGCircle radius={daysRadius} />
						{timerDays}
						<span>Days</span>
					</div>
				)}
				{timerHours >= 0 && (
					<div className="countdownItem">
						<SVGCircle radius={hoursRadius} />
						{timerHours}
						<span>Hours</span>
					</div>
				)}
				{timerMinutes >= 0 && (
					<div className="countdownItem">
						<SVGCircle radius={minutesRadius} />
						{timerMinutes}
						<span>Minutes</span>
					</div>
				)}
				{timerSeconds >= 0 && (
					<div className="countdownItem">
						<SVGCircle radius={secondsRadius} />
						{Math.round(timerSeconds)}
						<span>Seconds</span>
					</div>
				)}
			</div>
			<div>
				<p>Until your tour departs!</p>
			</div>
		</>
	);
};
//code for displaying the SVG

export default Countdown;
