import React, { useState } from "react";

const SignIn = (props) => {
	const [email, setEmail] = useState("");
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [bookingRef, setBookingRef] = useState("");
	//placing all error messages in an object
	const [firstNameError, setFirstNameError] = useState({});
	const [lastNameError, setLastNameError] = useState({});
	const [emailError, setEmailError] = useState({});
	const [bookingRefError, setBookingRefError] = useState({});

	const onSubmit = (e) => {
		// e.preventDefault(); << doesn't like this code...?
		const isValid = userValidation();
	};

	const userValidation = () => {
		const firstNameError = {};
		const lastNameError = {};
		const emailError = {};
		const bookingRefError = {};
		var isValid = true;

		if (firstName.length < 3 || firstName.length > 15) {
			firstNameError.firstNameMessage =
				"First name must be between 3-15 characters.";
			isValid = false;
		}
		if (lastName.length < 2 || lastName.length > 20) {
			lastNameError.lastNameMessage =
				"Last name must be between 2-20 characters.";
			isValid = false;
		}
		if (
			bookingRef.length < 8 ||
			bookingRef.length > 20 ||
			!bookingRef.includes("TBT-")
		) {
			bookingRefError.bookingRefMessage =
				"Your Booking Reference must be between 8-20 characters and start TBT-.";
			isValid = false;
		}
		if (!email.includes("@")) {
			emailError.emailMessage = "Please enter a valid email address.";
			isValid = false;
		}
		return true;

		//updating the error objects
		setFirstNameError(firstNameError);
		setLastNameError(lastNameError);
		setBookingRefError(bookingRefError);
		setEmailError(emailError);
		return isValid;
	};

	return (
		<>
			<div className="signIn">
				<h1>Sign in to view your booking</h1>
				<p>
					Please use the details you provided when booking your tour. Unsure?
					Check your confirmation email.
				</p>
				<div className="signInContainer">
					<form onSubmit={onSubmit}>
						<div>
							<label>First Name</label>
							<input
								// onSubmit={onSubmit}
								value={firstName}
								type="text"
								onChange={(e) => setFirstName(e.target.value)}
							/>
							{Object.keys(firstNameError).map((key) => {
								//mapping over the first name object
								return <div key={key}>{firstNameError[key]}</div>;
							})}
						</div>
						<div>
							<label>Last Name</label>
							<input
								// onSubmit={onSubmit}
								value={lastName}
								type="text"
								onChange={(e) => setLastName(e.target.value)}
							/>
							{Object.keys(lastNameError).map((key) => {
								return (
									<div key={key} style={{ color: "red" }}>
										{lastNameError[key]}
									</div>
								);
							})}
						</div>
						<div>
							<label>Booking Reference</label>
							<input
								// onSubmit={onSubmit}
								value={bookingRef}
								type="text"
								placeholder="TBT-"
								onChange={(e) => setBookingRef(e.target.value)}
							/>
							{Object.keys(bookingRefError).map((key) => {
								return (
									<div key={key} style={{ color: "red" }}>
										{bookingRefError[key]}
									</div>
								);
							})}
						</div>
						<div>
							<label>Email</label>
							<input
								// onSubmit={onSubmit}
								value={email}
								type="text"
								onChange={(e) => setEmail(e.target.value)}
							/>
							{Object.keys(emailError).map((key) => {
								return (
									<div key={key} style={{ color: "red" }}>
										{emailError[key]}
									</div>
								);
							})}
						</div>
						<p>
							I agree to the{" "}
							<a href="https://www.timberbush-tours.co.uk/terms-conditions">
								Terms
							</a>{" "}
							<input type="checkbox" />
						</p>
					</form>
				</div>
			</div>
			<button
				type="submit"
				onClick={() => {
					onSubmit();
					props.setPage(3);
				}}
			>
				View My Booking
			</button>
		</>
	);
};

export default SignIn;
