import React from "react";
import Joi from "Joi";

const SignIn = () => {
	const schema = Joi.object({
		name: Joi.string().required,
		email: Joi.string().email({
			minDomainSegments: 2,
			tlds: { allow: ["com", "net"] },
		}).required,
		bookingRef: Joi.string().required,
	});
	//tofinish the validation on below
	return (
		<>
			<h1>Sign in to view your tour details</h1>
			Name
			<input type="text" />
			Booking Reference
			<input type="text" />
			Email
			<input type="text" />
			Terms and Conditions
			<input type="checkbox" />
			<button onClick={() => setPage(3)}>Create Account</button>
		</>
	);
};

export default SignIn;
