import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
	faMale,
	faPhone,
	faAt,
	faComments,
} from "@fortawesome/free-solid-svg-icons";
import { faAcquisitionsIncorporated } from "@fortawesome/free-brands-svg-icons";
import axios from "axios";
// import axios from "axios";

const ContactForm = () => {
	//collect data from the form
	const [data, setData] = useState({
		firstName: "",
		lastName: "",
		email: "",
		tel: "",
		message: "",
		sent: false,
		error: "",
	});

	const handleChange = (e) => {
		const { name, value } = e.target;
		setData({
			...data,
			[name]: value,
		});
	};

	const resetForm = () => {
		setData({
			firstName: "",
			lastName: "",
			email: "",
			tel: "",
			message: "",
		});
	};

	const formSubmit = (e) => {
		e.preventDefault();

		setData({
			...data,
		});

		axios
			.post("/send", data)
			.then((res) => {
				if (res.data.result !== "success") {
					setData({
						...data,
						sent: false,
						error: "fail",
					});
					setTimeout(() => {
						resetForm();
					}, 6000);
				} else {
					setData({
						...data,
						sent: true,
						buttonText: sent,
						error: "success",
					});
					setTimeout(() => {
						resetForm();
					}, 6000);
				}
			})
			.catch((error) => {
				console.log(error.response.status);
			});
	};

	return (
		<>
			<form id="contactForm">
				<h2>Send us a Message</h2>
				<div className="formGroup">
					<label for="firstName">
						First Name<span>*</span>
					</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faMale" icon={faMale} />
						</span>
						<input
							type="text"
							name="firstName"
							id="firstName"
							value={data.firstName}
							onChange={handleChange}
						/>
					</div>
				</div>
				<div className="formGroup">
					<label for="lastName">
						Last Name<span>*</span>
					</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faMale" icon={faMale} />
						</span>
						<input
							type="text"
							name="lastName"
							id="lastName"
							value={data.lastName}
							onChange={handleChange}
						/>
					</div>
				</div>
				<div className="formGroup">
					<label for="phone">Phone</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faPhone" icon={faPhone} />
						</span>
						<input
							type="tel"
							name="tel"
							id="tel"
							value={data.tel}
							onChange={handleChange}
						/>
					</div>
				</div>
				<div className="formGroup">
					<label for="email">Email</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faAt" icon={faAt} />
						</span>
						<input
							type="email"
							name="email"
							id="email"
							value={data.email}
							onChange={handleChange}
						/>
					</div>
				</div>
				<div className="formGroup">
					<label for="message">Your Message</label>
					<div className="contactInput">
						<span className="formIcon">
							<FontAwesomeIcon className="faComments" icon={faComments} />
						</span>
						<textarea
							id="customerMessage"
							className="customerMessage"
							required=""
							rows="3"
							value={data.message}
							onChange={handleChange}
						></textarea>
					</div>
				</div>
				<div>
					<button
						type="submit"
						className="buttonContact"
						onClick={() => {
							console.log("I've been clicked");
						}}
					>
						Send
					</button>
				</div>
			</form>
		</>
	);
};

export default ContactForm;
