import React, { useState } from "react";

const SignIn = () => {
	const [details, setDetails] = useState({
		name: "",
		email: "",
		bookingRef: "",
	});

	const submitHandler = (e) => {
		e.preventDefault();
		Login(details);
	};

	return (
		<>
			<form onSubmit={submitHandler}>
				<div className="formInner">
					<h2>Sign In</h2>
					{/* ERROR MESSAGE HERE */}
					<div className="formGroup">
						<label htmlFor="name">Name:</label>
						<input
							type="text"
							name="name"
							id="name"
							onChange={(e) => setDetails({ ...details, name: e.target.value })} //once changed, call function, pass event through object 'details' - updates setDetails and new value for name
							value={details.name}
						/>
					</div>
					<div className="formGroup">
						<label htmlFor="email">Email:</label>
						<input
							type="email"
							name="email"
							id="email"
							onChange={(e) =>
								setDetails({ ...details, email: e.target.value })
							}
							value={details.email}
						/>
					</div>
					<div className="formGroup">
						<label htmlFor="bookingRef">Booking Reference:</label>
						<input
							type="text"
							name="bookingRef"
							id="bookingRef"
							onChange={(e) =>
								setDetails({ ...details, bookingRef: e.target.value })
							}
							value={details.bookingRef}
						/>
					</div>
					<input type="submit" value="login" />
				</div>
			</form>
		</>
	);
};

export default SignIn;
