import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const Navigation = () => {
	const [showMenu, setShowMenu] = useState(false); //deciding whether we will show the menu. Menu always loads in closed.

	let menu = <div>This is the menu</div>;

	return (
		<>
			<div>
				<FontAwesomeIcon
					icon={faBars}
					size="2x"
					onClick={() => setShowMenu(!showMenu)} //toggle
				/>
			</div>

			{menu}
		</>
	);
};

export default Navigation;
